document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".sidebar-link").forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();e=this.getAttribute("href").substring(1);document.querySelectorAll(".content-area").forEach(e=>{e.style.display="none"}),document.querySelectorAll(".introduction-area").forEach(e=>{e.style.display="none"}),document.getElementById(e).style.display="block"})}),document.querySelectorAll(".copy-code-btn").forEach(function(e){e.addEventListener("click",function(){var e=this.closest(".code-container").querySelector("code").innerText,t=this;navigator.clipboard.writeText(e).then(function(){t.innerHTML='<i class="fa-solid fa-check"></i>  Copied!'}).catch(function(e){console.error("Unable to copy code",e)})})})});